import React from "react";
import { connect } from "react-redux";
import PropTypes from "prop-types";
import Feedback from "./Feedback";
import {
  selectFeedback,
  updateFeedbackFormUsage,
  getFeedback,
} from "../actions"; //getFeedback was originally getEvents

class FeedbackList extends React.Component {
  constructor(props) {
    super(props);
    console.log("in FeedbackList: " + JSON.stringify(this.props.allfeedback));
  }

  componentDidMount() {
    if (!this.props.fetched) {
      this.props.fetchRules(this.props.fetched);
    }
    console.log("mount it!");
  }

  static propTypes = {
    allfeedback: PropTypes.arrayOf(
      PropTypes.shape({
        id: PropTypes.number.isRequired,
      }).isRequired
    ).isRequired,
  };

  render() {
    return (
      <div id="feedback-list" className="card bg-light">
        <h4 className="card-header">List of Feedback/Questions</h4>
        <table className="table">
          <thead>
            <tr>
              <th>Body_Text</th>
              <th>Reply_Text</th>
            </tr>
          </thead>
          <tbody>
            {this.props.allfeedback.map((feedback) => (
              <Feedback
                key={feedback.id}
                feedback={feedback}
                selectedId={this.props.appState.selectedId}
                onClick={() => this.props.onFeedbackClick(feedback)}
              />
            ))}
          </tbody>
        </table>
        <hr></hr>
        <p style={{ paddingLeft: "10px" }}>
          <input
            style={{ width: "fit-content" }}
            className="btn btn-primary"
            type={"button"}
            onClick={this.props.handleNewFeedbackClick}
            value="New Feedback"
          />
        </p>
      </div>
    );
  }
}

const mapStateToProps = (state) => {
  return {
    allfeedback: state.allfeedback,
    appState: state.appState,
  };
};

const mapDispatchToProps = (dispatch) => {
  return {
    fetchRules: (fetched) => {
      console.log("in FeedbackList.fetchRules:");
      dispatch(getFeedback(dispatch));
      fetched = true;
    },
    onFeedbackClick: (feedback) => {
      console.log(
        "in FeedbackList.onFeedbackClick:" + JSON.stringify(feedback)
      );
      dispatch(selectFeedback(feedback));
      dispatch(updateFeedbackFormUsage("view", feedback));
    },
    handleNewFeedbackClick: () => {
      console.log("in FeedbackList.handleNewFeedbackClick:");
      dispatch(updateFeedbackFormUsage("add"));
      dispatch(selectFeedback(-1));
    },
  };
};

const VisibleFeedbackList = connect(
  mapStateToProps,
  mapDispatchToProps
)(FeedbackList);

export default VisibleFeedbackList;
