import React from "react";
import { connect } from "react-redux";
import PropTypes from "prop-types";
import { updateFeedbackFormObject } from "../actions";
import { updateFeedbackFormUsage } from "../actions";
import { addFeedback, selectFeedback } from "../actions";
import RestAPI from "../rest";
import { deleteFeedback } from "../actions";
const restapi = new RestAPI();

const FeedbackForm = ({
  feedback,
  usage,
  handleChange,
  handleFeedbackEditClick,
  handleEditCancelClick,
  handleFeedbackSaveClick,
  handleFeedbackDeleteClick,
}) => (
  <div id="feedback-form" className="card bg-light" hidden={usage === "none"}>
    <div>
      <h4 className="card-header">Add Feedback or Questions</h4>
      <form>
        <table className="table">
          <tbody>
            {$user.IS_MANAGER ? (
              <tr>
                <td>Body_Text:</td>
                <td>
                  <input
                    type={"text"}
                    name={"body_text"}
                    onChange={handleChange}
                    placeholder={"Body_Text"}
                    value={feedback.code}
                    disabled={usage === "none" || usage === "view"}
                  />
                </td>
              </tr>
            ) : (
              <tr>
                <td>Reply_Text:</td>
                <td>
                  <input
                    type={"text"}
                    name={"reply_text"}
                    onChange={handleChange}
                    placeholder={"Reply_Text"}
                    value={feedback.title}
                    disabled={usage === "none" || usage === "view"}
                  />
                </td>
              </tr>
            )}
          </tbody>
        </table>
        <input
          type={"button"}
          value="Delete"
          onClick={(e) => handleFeedbackDeleteClick(e, feedback)}
          hidden={usage === "none" || usage === "view" || usage === "add"}
        />
        <input
          type={"button"}
          value="Save"
          onClick={(e) => handleFeedbackSaveClick(e, feedback)}
          hidden={usage === "none" || usage === "view"}
        />
        <input
          type={"button"}
          value="Cancel"
          onClick={(e) => handleEditCancelClick(e, feedback)}
          hidden={usage === "none" || usage === "view"}
        />
        <input
          type={"button"}
          value="Edit"
          onClick={(e) => handleFeedbackEditClick(e, feedback)}
          hidden={usage === "none" || usage === "edit" || usage === "add"}
        />
      </form>
    </div>
  </div>
);

FeedbackForm.propTypes = {
  feedback: PropTypes.shape({
    code: PropTypes.string.isRequired,
    title: PropTypes.string.isRequired,
  }).isRequired,
};

const mapStateToProps = (state) => {
  return {
    feedback: state.formState.feedback,
    usage: state.formState.usage,
    appState: state.appState,
  };
};

const mapDispatchToProps = (dispatch) => {
  return {
    handleChange: (e) => {
      const field_name = e.currentTarget.name;
      const field_value = e.currentTarget.value;
      dispatch(updateFeedbackFormObject(field_name, field_value));
    },
    handleFeedbackEditClick: (e, feedback, usage) => {
      console.log("in FeedbackForm.handleFeedbackEditClick");
      dispatch(updateFeedbackFormUsage("edit", feedback));
    },
    handleEditCancelClick: (e, feedback, usage) => {
      console.log("in FeedbackForm.handleEditCancelClick");
      dispatch(updateFeedbackFormUsage("view", feedback));
    },
    handleFeedbackSaveClick: (e, feedback, usage) => {
      console.log("in FeedbackForm.handleFeedbackSaveClick");
      dispatch(addFeedback(feedback));
      if (usage === "add") {
        dispatch(selectFeedback(feedback));
      }
      dispatch(updateFeedbackFormUsage("none"));
      restapi.postFeedback(dispatch, feedback);
    },
    handleFeedbackDeleteClick: (e, feedback) => {
      console.log("in FeedbackForm.handleEventDeleteClick");
      console.log("feedback: " + JSON.stringify(feedback));
      dispatch(deleteFeedback(feedback));
      dispatch(updateFeedbackFormUsage("none"));
      restapi.deleteFeedback(dispatch, feedback);
    },
  };
};

const VisibleFeedbackForm = connect(
  mapStateToProps,
  mapDispatchToProps
)(FeedbackForm);

export default VisibleFeedbackForm;
