const initial = {
  usage: "none",
  user: { id: "", name: "", email: "" },
  event: { id: "-1", code: "", title: "", description: "" },
  registration: {
    id: "-1",
    event_id: "-1",
    user_id: "-1",
    registration_date: -1,
    notes: "",
  },
};

const formState = (state = initial, action, data) => {
  switch (action.type) {
    case "UPDATE_USER_FORM_OBJECT": {
      let field = {};
      field[action.field_name] = action.field_value;
      let user = Object.assign({}, state.user, field);
      const obj = Object.assign({}, state, { user });
      return obj;
    }

    case "UPDATE_USER_FORM_USAGE": {
      let usage = { usage: action.usage };
      let new_user = { user: { id: "-1", name: "", email: "" } };

      console.log(
        "UPDATE_USER_FORM_USAGE: (" +
          action.usage +
          ") action.user: " +
          JSON.stringify(action.user)
      );

      if (action.usage === "view" || action.usage === "edit") {
        new_user = { user: Object.assign({}, action.user) };
      } else {
        let id = Math.floor(1000 * (1 + Math.random(Date.now()))).toString();
        new_user = { user: { id: id, name: "", email: "" } };
      }

      return Object.assign({}, state, new_user, usage);
    }

    case "UPDATE_EVENT_FORM_OBJECT": {
      let field = {};
      field[action.field_name] = action.field_value;
      let event = Object.assign({}, state.event, field);
      const obj = Object.assign({}, state, { event });
      return obj;
    }

    case "UPDATE_EVENT_FORM_USAGE": {
      let usage = { usage: action.usage };
      let new_event = {
        event: { id: -1, code: "", title: "", description: "" },
      };

      console.log(
        "UPDATE_EVENT_FORM_USAGE: (" +
          action.usage +
          ") action.event: " +
          JSON.stringify(action.event)
      );

      if (action.usage === "view" || action.usage === "edit") {
        new_event = { event: Object.assign({}, action.event) };
      } else {
        let id = Math.floor(1000 * (1 + Math.random(Date.now())));
        new_event = { event: { id: id, code: "", title: "", description: "" } };
      }

      return Object.assign({}, state, new_event, usage);
    }

    case "UPDATE_REGISTRATION_FORM_OBJECT": {
      let field = {};
      field[action.field_name] = action.field_value;
      let registration = Object.assign({}, state.registration, field);
      const obj = Object.assign({}, state, { registration });
      return obj;
    }

    case "UPDATE_REGISTRATION_FORM_USAGE": {
      let usage = { usage: action.usage };
      let new_registration = {
        registration: {
          id: -1,
          event_id: "-1",
          user_id: "-1",
          registration_date: -1,
          notes: "",
        },
      };

      console.log(
        "UPDATE_REGISTRATION_FORM_USAGE: (" +
          action.usage +
          ") action.registration: " +
          JSON.stringify(action.registration)
      );

      if (action.usage === "view" || action.usage === "edit") {
        new_registration = {
          registration: Object.assign({}, action.registration),
        };
      } else {
        let id = Math.floor(1000 * (1 + Math.random(Date.now())));
        new_registration = {
          registration: {
            id: id,
            event_id: "-1",
            user_id: "-1",
            registration_date: -1,
            notes: "",
          },
        };
      }

      return Object.assign({}, state, new_registration, usage);
    }

    default:
      return state;
  }
};

export default formState;
