import React, { useState } from "react";

const FeedbackForm = ({ userId }) => {
  const [user, setUser] = useState();
  /* USER REQUESTS */

  useEffect(() => {
    var myInit = { method: "GET", mode: "cors" };

    let promise = fetch(`/api/users/${userId}`, myInit);
    promise
      .then((response) => {
        return response.text();
      })
      .then(function (text) {
        console.log("getUsers Request successful: ", text);
        let response = JSON.parse(text);
        setUser(response);
      });
  }, []);

  function handleFeedbackSaveClick(e, feedback) {
    console.log(e);
    let myInit = { method: "POST", mode: "cors" };
    let data = { comment: e.form.comment };
    let saveClick = fetch(``, myInit, data);
    saveClick
      .then((response) => {
        return response.text();
      })
      .then(function (text) {
        let response = JSON.parse(text);
      });
  }

  return (
    <div id="feedback-form" className="card bg-light" hidden={usage === "none"}>
      <div>
        <h4 className="card-header">Add Feedback or Questions</h4>
        <form>
          <table className="table">
            <tbody>
              {user.IS_MANAGER ? (
                <tr>
                  <td>Body_Text:</td>
                  <td>
                    <input
                      type={"text"}
                      name={"body_text"}
